mapper.select:
SELECT COUNT(*) AS total_count
FROM board_info


            category_id = #{categoryId}


            AND title LIKE CONCAT('%', #{searchKeyword}, '%')


            AND system_register_datetime BETWEEN #{startDate} AND #{endDate}

SELECT bi.category_id, (SELECT ci.category FROM category_info ci WHERE ci.category_id = bi.category_id) AS category_name, bi.writer, bi.password, bi.title, bi.content, bi.view_count, bi.system_register_datetime, bi.system_update_datetime FROM board_info bi
bi.category_id = #{categoryId}
AND bi.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND bi.system_register_datetime BETWEEN #{startDate} AND #{endDate}
